CREATE TABLE finance_studio (
    digital_id INT,                  
    product_id INT,                  
    user_id INT,                     
    units_sold INT,                  
    brand VARCHAR(100),              
    sales DECIMAL(15, 2),            
    customer_id INT,                 
    transaction_id INT,              
    currency VARCHAR(10),            
    customer_name VARCHAR(100),      
    customer_email VARCHAR(100),     
    net_profit DECIMAL(15, 2),       
    quantity INT,                    
    sale_date DATETIME              
);


SELECT *
FROM finance_studio
