CREATE TABLE products (
    product_id INT,
    sku VARCHAR(50),
    product_name VARCHAR(100),
    category VARCHAR(50),
    size VARCHAR(20),
    color VARCHAR(30),
    price DECIMAL(10, 2),
    description TEXT,
    brand VARCHAR(50),
    rating FLOAT,
    is_active VARCHAR(50),
    texture VARCHAR(50),
    material VARCHAR(50),
    image VARCHAR(255),
    style VARCHAR(50),
    caption VARCHAR(255),
    CreateTime DATETIME,
    UpdateTime DATETIME,
	tags VARCHAR(50)
);



SELECT *
FROM products

